function logout() {
  $.ajax({
    type: "POST",
    url: '/arena/users/logout',
    data: { },
    success: function(data) {
      window.location.href="/arena";
    }, async: false });
}

function changePassword(ID) {
  $("#showDiv-" + ID).css({"display" : "block"});
}

function hasWhiteSpace(s) {
  return /\s/g.test(s);
}

function updatePassword(ID) {
  let newPassword = $("#newPassword-" + ID).val();
  if(newPassword.length <= 7) {
    $("#passwordCheck-" + ID).empty();
    $("#passwordCheck-" + ID).removeClass();
    $("#passwordCheck-" + ID).addClass("alert alert-danger");
    $("#passwordCheck-" + ID).append("Must be atleast 8 characters");    
    return;
  } else if(hasWhiteSpace(newPassword) == true) {
    $("#passwordCheck-" + ID).empty();
    $("#passwordCheck-" + ID).removeClass();
    $("#passwordCheck-" + ID).addClass("alert alert-danger");
    $("#passwordCheck-" + ID).append("Must not have any spaces or tabs");            
  } else {
    if(regex.test(newPassword) == false) {
      $("#passwordCheck-" + ID).empty();
      $("#passwordCheck-" + ID).removeClass();
      $("#passwordCheck-" + ID).addClass("alert alert-danger");
      $("#passwordCheck-" + ID).append("Must be atleast 1 special characters");          
      return;      
    } else {
      $("#passwordCheck-" + ID).empty();
      $("#passwordCheck-" + ID).removeClass();
      $.ajax({
        type: "POST",
        url: '/arena/home/updateuserpassword/' + ID,
        data: {password: newPassword },
        success: function(addedUser) {
          $("#udpatedPassword").empty();
          $("#udpatedPassword-" + ID).removeClass();
          $("#udpatedPassword-" + ID).addClass("alert alert-success");
          $("#udpatedPassword-" + ID).append("Password successfully updated. It is recommended to <a href='#' onclick='logout()'>Re Login to aRENA</a>");          
      }, async: true });
    }
  }
}

